import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import Sidebar from "@/components/layout/sidebar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { DataTable } from "@/components/ui/data-table";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";
import { Label } from "@/components/ui/label";
import { Search, Edit, AlertTriangle, History, Plus, Trash2, PlusCircle, AlertCircle } from "lucide-react";
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { authenticatedApiRequest } from "@/lib/auth";
import { format } from "date-fns";

export default function Stock() {
  const [searchTerm, setSearchTerm] = useState("");
  const [editingStock, setEditingStock] = useState<any>(null);
  const [selectedItem, setSelectedItem] = useState<any>(null);
  const [dateFilter, setDateFilter] = useState({
    startDate: "",
    endDate: ""
  });
  const [showManualEntry, setShowManualEntry] = useState(false);
  const [showWastageEntry, setShowWastageEntry] = useState(false);
  const [quantities, setQuantities] = useState({ crates: "", kgs: "", boxes: "" });
  const [manualEntry, setManualEntry] = useState({
    vendorId: "",
    notes: "",
    lineItems: [{ itemId: "", crates: "", kgs: "", boxes: "" }] as Array<{itemId: string, crates: string, kgs: string, boxes: string}>,
  });
  const [wastageEntry, setWastageEntry] = useState({
    itemId: "",
    crates: "",
    kgs: "",
    boxes: "",
    reason: "",
    notes: "",
  });
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: stock, isLoading } = useQuery<any[]>({
    queryKey: ["/api/stock"],
  });

  const { data: stockMovements, isLoading: movementsLoading } = useQuery<any[]>({
    queryKey: ["/api/stock-movements"],
  });

  const { data: itemMovements } = useQuery<any[]>({
    queryKey: ["/api/stock-movements/item", selectedItem?.itemId],
    enabled: !!selectedItem?.itemId,
  });

  const { data: items } = useQuery<any[]>({
    queryKey: ["/api/items"],
  });

  const { data: vendors } = useQuery<any[]>({
    queryKey: ["/api/vendors"],
  });

  const updateStockMutation = useMutation({
    mutationFn: async ({ itemId, data }: { itemId: string; data: any }) => {
      const response = await authenticatedApiRequest("PUT", `/api/stock/${itemId}`, data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Stock updated",
        description: "Stock quantities have been updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/stock"] });
      setEditingStock(null);
      setQuantities({ crates: "", kgs: "", boxes: "" });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to update stock",
        variant: "destructive",
      });
    },
  });

  const createStockMovementMutation = useMutation({
    mutationFn: async (movementData: any) => {
      const response = await authenticatedApiRequest("POST", "/api/stock-movements", movementData);
      return response.json();
    },
    onSuccess: (data, variables) => {
      // Check if this is a wastage entry
      if (variables.referenceType === "WASTAGE") {
        toast({
          title: "Wastage recorded",
          description: "Stock wastage has been recorded successfully",
        });
        queryClient.invalidateQueries({ queryKey: ["/api/stock"] });
        queryClient.invalidateQueries({ queryKey: ["/api/stock-movements"] });
        setShowWastageEntry(false);
        setWastageEntry({ itemId: "", crates: "", kgs: "", boxes: "", reason: "", notes: "" });
      } else {
        toast({
          title: "Stock entry added",
          description: "Manual stock entry has been added successfully",
        });
        queryClient.invalidateQueries({ queryKey: ["/api/stock"] });
        queryClient.invalidateQueries({ queryKey: ["/api/stock-movements"] });
        setShowManualEntry(false);
        setManualEntry({ vendorId: "", notes: "", lineItems: [{ itemId: "", crates: "", kgs: "", boxes: "" }] });
      }
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to add stock entry",
        variant: "destructive",
      });
    },
  });

  const filteredStock = stock?.filter((item: any) => {
    const searchString = searchTerm.toLowerCase();
    return (
      item.item.name.toLowerCase().includes(searchString) ||
      item.item.quality.toLowerCase().includes(searchString) ||
      (item.item.vendor?.name || '').toLowerCase().includes(searchString)
    );
  }) || [];

  const isLowStock = (item: any) => {
    const totalQty = parseFloat(item.quantityInKgs) + parseFloat(item.quantityInCrates) + parseFloat(item.quantityInBoxes || 0);
    return totalQty < 20 && totalQty > 0;
  };

  // Define stock table columns
  const stockColumns = [
    {
      accessorKey: "item.name",
      header: "Item",
      cell: (value: string) => <div className="font-medium">{value}</div>,
    },
    {
      accessorKey: "item.quality",
      header: "Quality",
    },
    {
      accessorKey: "item.vendor.name",
      header: "Vendor",
      cell: (value: string) => value || "Unknown Vendor",
    },
    {
      accessorKey: "quantityInCrates",
      header: "Quantity (Crates)",
      cell: (value: string) => parseFloat(value).toFixed(2),
    },
    {
      accessorKey: "quantityInBoxes",
      header: "Quantity (Boxes)",
      cell: (value: string) => parseFloat(value || "0").toFixed(2),
    },
    {
      accessorKey: "quantityInKgs",
      header: "Quantity (Kgs)",
      cell: (value: string) => parseFloat(value).toFixed(2),
    },
    {
      accessorKey: "lastUpdated",
      header: "Last Updated",
      cell: (value: string) => format(new Date(value), "MMM dd, yyyy"),
    },
    {
      accessorKey: "id",
      header: "Status",
      cell: (value: string, row: any) => {
        if (isLowStock(row)) {
          return (
            <div className="flex items-center space-x-1">
              <AlertTriangle className="h-4 w-4 text-chart-1" />
              <Badge variant="destructive">Low Stock</Badge>
            </div>
          );
        }
        return <Badge variant="default">Available</Badge>;
      },
    },
    {
      accessorKey: "id",
      header: "Actions",
      cell: (value: string, row: any) => (
        <div className="flex space-x-2">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleEditStock(row)}
            data-testid={`button-edit-stock-${value}`}
            title="Edit Stock"
          >
            <Edit className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setSelectedItem(row)}
            data-testid={`button-history-${value}`}
            title="View History"
          >
            <History className="h-4 w-4" />
          </Button>
        </div>
      ),
    },
  ];

  const handleEditStock = (item: any) => {
    setEditingStock(item);
    setQuantities({
      crates: item.quantityInCrates,
      kgs: item.quantityInKgs,
      boxes: item.quantityInBoxes || "0",
    });
  };

  const handleUpdateStock = () => {
    if (!editingStock) return;

    updateStockMutation.mutate({
      itemId: editingStock.itemId,
      data: {
        quantityInCrates: quantities.crates,
        quantityInKgs: quantities.kgs,
      },
    });
  };

  const handleManualStockEntry = async () => {
    if (!manualEntry.vendorId) {
      toast({
        title: "Missing information",
        description: "Please select a vendor",
        variant: "destructive",
      });
      return;
    }

    const validLineItems = manualEntry.lineItems.filter(item => 
      item.itemId && (item.crates || item.kgs)
    );

    if (validLineItems.length === 0) {
      toast({
        title: "Missing information",
        description: "Please add at least one item with quantities",
        variant: "destructive",
      });
      return;
    }

    try {
      // Create stock movements for each line item
      for (const lineItem of validLineItems) {
        await createStockMovementMutation.mutateAsync({
          itemId: lineItem.itemId,
          movementType: "IN",
          quantityInCrates: lineItem.crates || "0",
          quantityInBoxes: lineItem.boxes || "0",
          quantityInKgs: lineItem.kgs || "0",
          referenceType: "MANUAL_ENTRY",
          referenceId: null,
          referenceNumber: "MANUAL",
          vendorId: manualEntry.vendorId,
          retailerId: null,
          notes: manualEntry.notes || "Manual stock entry",
          movementDate: new Date().toISOString(),
        });
      }

      toast({
        title: "Stock entries added",
        description: `Successfully added ${validLineItems.length} stock entries`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/stock"] });
      queryClient.invalidateQueries({ queryKey: ["/api/stock-movements"] });
      setShowManualEntry(false);
      setManualEntry({ vendorId: "", notes: "", lineItems: [{ itemId: "", crates: "", kgs: "", boxes: "" }] });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to add stock entries",
        variant: "destructive",
      });
    }
  };

  const addLineItem = () => {
    setManualEntry({
      ...manualEntry,
      lineItems: [...manualEntry.lineItems, { itemId: "", crates: "", kgs: "", boxes: "" }]
    });
  };

  const removeLineItem = (index: number) => {
    if (manualEntry.lineItems.length > 1) {
      setManualEntry({
        ...manualEntry,
        lineItems: manualEntry.lineItems.filter((_, i) => i !== index)
      });
    }
  };

  const updateLineItem = (index: number, field: string, value: string) => {
    const updatedLineItems = [...manualEntry.lineItems];
    updatedLineItems[index] = { ...updatedLineItems[index], [field]: value };
    setManualEntry({ ...manualEntry, lineItems: updatedLineItems });
  };

  const filteredItems = items?.filter((item: any) => 
    !manualEntry.vendorId || item.vendorId === manualEntry.vendorId
  ) || [];

  const handleWastageEntry = () => {
    if (!wastageEntry.itemId || (!wastageEntry.crates && !wastageEntry.boxes && !wastageEntry.kgs)) {
      toast({
        title: "Missing information",
        description: "Please select an item and enter wastage quantities",
        variant: "destructive",
      });
      return;
    }

    if (!wastageEntry.reason) {
      toast({
        title: "Missing information", 
        description: "Please provide a reason for wastage",
        variant: "destructive",
      });
      return;
    }

    createStockMovementMutation.mutate({
      itemId: wastageEntry.itemId,
      movementType: "OUT",
      quantityInCrates: wastageEntry.crates || "0",
      quantityInBoxes: wastageEntry.boxes || "0",
      quantityInKgs: wastageEntry.kgs || "0",
      referenceType: "WASTAGE",
      referenceId: null,
      referenceNumber: "WASTAGE",
      vendorId: null,
      retailerId: null,
      notes: `Wastage: ${wastageEntry.reason}${wastageEntry.notes ? ` - ${wastageEntry.notes}` : ''}`,
      movementDate: new Date().toISOString(),
    });
  };

  return (
    <div className="flex h-screen">
      <Sidebar />

      <div className="flex-1 flex flex-col">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-semibold text-foreground">Stock Management</h2>
              <p className="text-sm text-muted-foreground">
                Monitor and update inventory levels
              </p>
            </div>
          </div>
        </header>

        {/* Content */}
        <main className="flex-1 overflow-auto p-6">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle>Current Stock</CardTitle>
                <div className="flex items-center space-x-4">
                  <Button
                    onClick={() => setShowManualEntry(true)}
                    data-testid="button-add-stock"
                  >
                    <Plus className="h-4 w-4 mr-2" />
                    Add Stock
                  </Button>
                  <Button 
                    variant="destructive"
                    onClick={() => setShowWastageEntry(true)}
                    data-testid="button-add-wastage"
                  >
                    <AlertCircle className="h-4 w-4 mr-2" />
                    Record Wastage
                  </Button>
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                    <Input
                      placeholder="Search stock..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      className="pl-10 w-64"
                      data-testid="input-search-stock"
                    />
                  </div>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <DataTable
                data={filteredStock}
                columns={stockColumns}
                searchTerm={searchTerm}
                searchFields={["item.name", "item.quality", "item.vendor.name"]}
                isLoading={isLoading}
                enableRowSelection={true}
                rowKey="id"
              />
            </CardContent>
          </Card>
        </main>
      </div>

      {/* Update Stock Modal */}
      <Dialog open={!!editingStock} onOpenChange={() => setEditingStock(null)}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Update Stock</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            {editingStock && (
              <div className="text-sm text-muted-foreground">
                {editingStock.item.name} - {editingStock.item.quality}
              </div>
            )}
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="crates">Quantity in Crates</Label>
                <Input
                  id="crates"
                  type="number"
                  step="0.01"
                  value={quantities.crates}
                  onChange={(e) => setQuantities({ ...quantities, crates: e.target.value })}
                  data-testid="input-quantity-crates"
                />
              </div>
              <div>
                <Label htmlFor="kgs">Quantity in Kgs</Label>
                <Input
                  id="kgs"
                  type="number"
                  step="0.01"
                  value={quantities.kgs}
                  onChange={(e) => setQuantities({ ...quantities, kgs: e.target.value })}
                  data-testid="input-quantity-kgs"
                />
              </div>
            </div>
            <div className="flex justify-end space-x-2">
              <Button 
                variant="outline" 
                onClick={() => setEditingStock(null)}
                data-testid="button-cancel-stock-update"
              >
                Cancel
              </Button>
              <Button 
                onClick={handleUpdateStock}
                disabled={updateStockMutation.isPending}
                data-testid="button-update-stock"
              >
                {updateStockMutation.isPending ? "Updating..." : "Update Stock"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Stock Movement History Modal */}
      <Dialog open={!!selectedItem} onOpenChange={() => {
        setSelectedItem(null);
        setDateFilter({ startDate: "", endDate: "" });
      }}>
        <DialogContent className="max-w-6xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Stock Movement History</DialogTitle>
          </DialogHeader>
          {selectedItem && (
            <div className="space-y-4">
              <div className="border-b pb-2">
                <h3 className="font-semibold">{selectedItem.item.name} - {selectedItem.item.quality}</h3>
                <p className="text-sm text-muted-foreground">Vendor: {selectedItem.item.vendor?.name || 'Unknown Vendor'}</p>
                <p className="text-sm text-muted-foreground">
                  Current Stock: {parseFloat(selectedItem.quantityInCrates).toFixed(2)} Crates, {parseFloat(selectedItem.quantityInKgs).toFixed(2)} Kgs
                </p>
              </div>

              <div>
                <div className="flex items-center justify-between mb-4">
                  <h4 className="font-medium">Movement History</h4>
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-2">
                      <Label htmlFor="start-date" className="text-sm font-medium whitespace-nowrap">From:</Label>
                      <Input
                        id="start-date"
                        type="date"
                        value={dateFilter.startDate}
                        onChange={(e) => setDateFilter({ ...dateFilter, startDate: e.target.value })}
                        className="w-40"
                        data-testid="input-start-date"
                      />
                    </div>
                    <div className="flex items-center space-x-2">
                      <Label htmlFor="end-date" className="text-sm font-medium whitespace-nowrap">To:</Label>
                      <Input
                        id="end-date"
                        type="date"
                        value={dateFilter.endDate}
                        onChange={(e) => setDateFilter({ ...dateFilter, endDate: e.target.value })}
                        className="w-40"
                        data-testid="input-end-date"
                      />
                    </div>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setDateFilter({ startDate: "", endDate: "" })}
                      data-testid="button-clear-filter"
                    >
                      Clear
                    </Button>
                  </div>
                </div>
{(() => {
                  // Filter movements based on date range
                  let filteredMovements = itemMovements || [];

                  if (dateFilter.startDate) {
                    filteredMovements = filteredMovements.filter((movement: any) => 
                      new Date(movement.movementDate) >= new Date(dateFilter.startDate)
                    );
                  }

                  if (dateFilter.endDate) {
                    filteredMovements = filteredMovements.filter((movement: any) => 
                      new Date(movement.movementDate) <= new Date(dateFilter.endDate + 'T23:59:59')
                    );
                  }

                  if (filteredMovements.length > 0) {
                    return (
                      <Accordion type="multiple" className="w-full">
                        {filteredMovements.map((movement: any, index: number) => (
                          <AccordionItem key={movement.id} value={`movement-${index}`}>
                            <AccordionTrigger className="hover:no-underline">
                              <div className="flex items-center justify-between w-full pr-4">
                                <div className="flex items-center space-x-4">
                                  <Badge variant={movement.movementType === "IN" ? "default" : "secondary"}>
                                    {movement.movementType === "IN" ? "Stock In" : "Stock Out"}
                                  </Badge>
                                  <span className="text-sm font-medium">
                                    {format(new Date(movement.movementDate), "MMM dd, yyyy")}
                                  </span>
                                  <span className={`text-sm font-semibold ${
                                    movement.movementType === "IN" ? "text-green-600" : "text-red-600"
                                  }`}>
                                    {movement.movementType === "IN" ? "+" : "-"}
                                    {parseFloat(movement.quantityInCrates).toFixed(2)} Crates, 
                                    {parseFloat(movement.quantityInKgs).toFixed(2)} Kgs
                                  </span>
                                </div>
                                <div className="text-right">
                                  <div className="text-sm text-muted-foreground">
                                    {movement.referenceNumber || movement.referenceType}
                                  </div>
                                  {movement.movementType === "OUT" && movement.retailer && (
                                    <div className="text-xs text-muted-foreground">
                                      Retailer: {movement.retailer.name}
                                    </div>
                                  )}
                                </div>
                              </div>
                            </AccordionTrigger>
                            <AccordionContent>
                              <div className="pt-2 pb-4 space-y-3">
                                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                                  <div>
                                    <span className="font-medium text-muted-foreground">Date:</span>
                                    <div>{format(new Date(movement.movementDate), "PPP")}</div>
                                  </div>
                                  <div>
                                    <span className="font-medium text-muted-foreground">Type:</span>
                                    <div>
                                      <Badge variant={movement.movementType === "IN" ? "default" : "secondary"} className="text-xs">
                                        {movement.movementType === "IN" ? "Stock In" : "Stock Out"}
                                      </Badge>
                                    </div>
                                  </div>
                                  <div>
                                    <span className="font-medium text-muted-foreground">Quantity:</span>
                                    <div className={movement.movementType === "IN" ? "text-green-600" : "text-red-600"}>
                                      {movement.movementType === "IN" ? "+" : "-"}
                                      {parseFloat(movement.quantityInCrates).toFixed(2)} Crates
                                      <br />
                                      {movement.movementType === "IN" ? "+" : "-"}
                                      {parseFloat(movement.quantityInKgs).toFixed(2)} Kgs
                                    </div>
                                  </div>
                                  <div>
                                    <span className="font-medium text-muted-foreground">Reference:</span>
                                    <div>{movement.referenceNumber || movement.referenceType}</div>
                                  </div>
                                </div>

                                {movement.movementType === "OUT" && movement.retailer && (
                                  <div className="bg-muted/50 rounded-lg p-3">
                                    <span className="font-medium text-muted-foreground text-sm">Retailer Information:</span>
                                    <div className="mt-1">
                                      <div className="font-medium">{movement.retailer.name}</div>
                                      {movement.retailer.contactPerson && (
                                        <div className="text-sm text-muted-foreground">Contact: {movement.retailer.contactPerson}</div>
                                      )}
                                    </div>
                                  </div>
                                )}

                                {movement.rate && (
                                  <div className="bg-blue-50 dark:bg-blue-950/20 rounded-lg p-3">
                                    <span className="font-medium text-muted-foreground text-sm">Rate Information:</span>
                                    <div className="mt-1 font-medium">â‚¹{parseFloat(movement.rate).toFixed(2)} per Kg</div>
                                  </div>
                                )}

                                {movement.notes && (
                                  <div>
                                    <span className="font-medium text-muted-foreground text-sm">Notes:</span>
                                    <div className="mt-1 text-sm bg-muted/30 rounded p-2">{movement.notes}</div>
                                  </div>
                                )}
                              </div>
                            </AccordionContent>
                          </AccordionItem>
                        ))}
                      </Accordion>
                    );
                  } else {
                    return (
                      <p className="text-center text-muted-foreground py-8">
                        {dateFilter.startDate || dateFilter.endDate ? 
                          "No movements found for the selected date range" : 
                          "No movement history found"
                        }
                      </p>
                    );
                  }
                })()}
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* Manual Stock Entry Modal */}
      <Dialog open={showManualEntry} onOpenChange={(open) => {
        if (!open) {
          setManualEntry({ vendorId: "", notes: "", lineItems: [{ itemId: "", crates: "", kgs: "", boxes: "" }] });
        }
        setShowManualEntry(open);
      }}>
        <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Add Stock Entry</DialogTitle>
          </DialogHeader>
          <div className="space-y-6">
            {/* Vendor Selection */}
            <div>
              <Label htmlFor="vendor-select">Select Vendor</Label>
              <Select 
                value={manualEntry.vendorId} 
                onValueChange={(value) => setManualEntry({ 
                  ...manualEntry, 
                  vendorId: value,
                  lineItems: [{ itemId: "", crates: "", kgs: "", boxes: "" }] // Reset line items when vendor changes
                })}
              >
                <SelectTrigger data-testid="select-vendor">
                  <SelectValue placeholder="Select a vendor" />
                </SelectTrigger>
                <SelectContent>
                  {vendors?.map((vendor: any) => (
                    <SelectItem key={vendor.id} value={vendor.id}>
                      {vendor.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Line Items */}
            {manualEntry.vendorId && (
              <div>
                <div className="flex items-center justify-between mb-4">
                  <Label className="text-base font-medium">Stock Items</Label>
                  <Button
                    type="button"
                    variant="outline"
                    size="sm"
                    onClick={addLineItem}
                    data-testid="button-add-line-item"
                  >
                    <PlusCircle className="h-4 w-4 mr-2" />
                    Add Item
                  </Button>
                </div>

                <div className="space-y-4">
                  {manualEntry.lineItems.map((lineItem, index) => (
                    <div key={index} className="border rounded-lg p-4">
                      <div className="flex items-center justify-between mb-3">
                        <Label className="text-sm font-medium">Item {index + 1}</Label>
                        {manualEntry.lineItems.length > 1 && (
                          <Button
                            type="button"
                            variant="ghost"
                            size="sm"
                            onClick={() => removeLineItem(index)}
                            data-testid={`button-remove-line-item-${index}`}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        )}
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                        <div>
                          <Label htmlFor={`item-select-${index}`}>Item</Label>
                          <Select 
                            value={lineItem.itemId} 
                            onValueChange={(value) => updateLineItem(index, "itemId", value)}
                          >
                            <SelectTrigger data-testid={`select-item-${index}`}>
                              <SelectValue placeholder="Select item" />
                            </SelectTrigger>
                            <SelectContent>
                              {filteredItems?.map((item: any) => (
                                <SelectItem key={item.id} value={item.id}>
                                  {item.name} - {item.quality}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                        </div>

                        <div>
                          <Label htmlFor={`crates-${index}`}>Crates</Label>
                          <Input
                            id={`crates-${index}`}
                            type="number"
                            step="0.01"
                            value={lineItem.crates}
                            onChange={(e) => updateLineItem(index, "crates", e.target.value)}
                            data-testid={`input-crates-${index}`}
                          />
                        </div>

                        <div>
                          <Label htmlFor={`boxes-${index}`}>Boxes</Label>
                          <Input
                            id={`boxes-${index}`}
                            type="number"
                            step="0.01"
                            value={lineItem.boxes}
                            onChange={(e) => updateLineItem(index, "boxes", e.target.value)}
                            data-testid={`input-boxes-${index}`}
                          />
                        </div>

                        <div>
                          <Label htmlFor={`kgs-${index}`}>Kgs</Label>
                          <Input
                            id={`kgs-${index}`}
                            type="number"
                            step="0.01"
                            value={lineItem.kgs}
                            onChange={(e) => updateLineItem(index, "kgs", e.target.value)}
                            data-testid={`input-kgs-${index}`}
                          />
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Notes */}
            <div>
              <Label htmlFor="manual-notes">Notes</Label>
              <Textarea
                id="manual-notes"
                placeholder="Enter any notes for this stock entry"
                value={manualEntry.notes}
                onChange={(e) => setManualEntry({ ...manualEntry, notes: e.target.value })}
                data-testid="textarea-manual-notes"
              />
            </div>

            {/* Actions */}
            <div className="flex justify-end space-x-2">
              <Button 
                variant="outline" 
                onClick={() => setShowManualEntry(false)}
                data-testid="button-cancel-manual-entry"
              >
                Cancel
              </Button>
              <Button 
                onClick={handleManualStockEntry}
                disabled={createStockMovementMutation.isPending}
                data-testid="button-add-manual-entry"
              >
                {createStockMovementMutation.isPending ? "Adding..." : "Add Stock"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Wastage Entry Modal */}
      <Dialog open={showWastageEntry} onOpenChange={(open) => {
        if (!open) {
          setWastageEntry({ itemId: "", crates: "", kgs: "", boxes: "", reason: "", notes: "" });
        }
        setShowWastageEntry(open);
      }}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Record Stock Wastage</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="wastage-item-select">Select Item</Label>
              <Select 
                value={wastageEntry.itemId} 
                onValueChange={(value) => setWastageEntry({ ...wastageEntry, itemId: value })}
              >
                <SelectTrigger data-testid="select-wastage-item">
                  <SelectValue placeholder="Select an item" />
                </SelectTrigger>
                <SelectContent>
                  {items?.map((item: any) => (
                    <SelectItem key={item.id} value={item.id}>
                      {item.name} - {item.quality} ({item.vendor?.name || 'Unknown Vendor'})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="grid grid-cols-3 gap-4">
              <div>
                <Label htmlFor="wastage-crates">Wastage in Crates</Label>
                <Input
                  id="wastage-crates"
                  type="number"
                  step="0.01"
                  value={wastageEntry.crates}
                  onChange={(e) => setWastageEntry({ ...wastageEntry, crates: e.target.value })}
                  data-testid="input-wastage-crates"
                />
              </div>
              <div>
                <Label htmlFor="wastage-boxes">Wastage in Boxes</Label>
                <Input
                  id="wastage-boxes"
                  type="number"
                  step="0.01"
                  value={wastageEntry.boxes}
                  onChange={(e) => setWastageEntry({ ...wastageEntry, boxes: e.target.value })}
                  data-testid="input-wastage-boxes"
                />
              </div>
              <div>
                <Label htmlFor="wastage-kgs">Wastage in Kgs</Label>
                <Input
                  id="wastage-kgs"
                  type="number"
                  step="0.01"
                  value={wastageEntry.kgs}
                  onChange={(e) => setWastageEntry({ ...wastageEntry, kgs: e.target.value })}
                  data-testid="input-wastage-kgs"
                />
              </div>
            </div>

            <div>
              <Label htmlFor="wastage-reason">Reason for Wastage *</Label>
              <Select 
                value={wastageEntry.reason} 
                onValueChange={(value) => setWastageEntry({ ...wastageEntry, reason: value })}
              >
                <SelectTrigger data-testid="select-wastage-reason">
                  <SelectValue placeholder="Select reason" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="Spoilage">Spoilage</SelectItem>
                  <SelectItem value="Damaged">Damaged/Broken</SelectItem>
                  <SelectItem value="Overripe">Overripe</SelectItem>
                  <SelectItem value="Pest Damage">Pest Damage</SelectItem>
                  <SelectItem value="Physical Damage">Physical Damage</SelectItem>
                  <SelectItem value="Quality Issues">Quality Issues</SelectItem>
                  <SelectItem value="Other">Other</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="wastage-notes">Additional Notes</Label>
              <Textarea
                id="wastage-notes"
                placeholder="Enter additional notes about the wastage"
                value={wastageEntry.notes}
                onChange={(e) => setWastageEntry({ ...wastageEntry, notes: e.target.value })}
                data-testid="textarea-wastage-notes"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <Button 
                variant="outline" 
                onClick={() => setShowWastageEntry(false)}
                data-testid="button-cancel-wastage"
              >
                Cancel
              </Button>
              <Button 
                variant="destructive"
                onClick={handleWastageEntry}
                disabled={createStockMovementMutation.isPending}
                data-testid="button-record-wastage"
              >
                {createStockMovementMutation.isPending ? "Recording..." : "Record Wastage"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/users.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import Sidebar from "@/components/layout/sidebar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import { DataTable } from "@/components/ui/data-table";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Users, Plus, Edit, Trash2, Shield } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { authenticatedApiRequest } from "@/lib/auth";
import { PERMISSIONS, ROLE_PERMISSIONS, permissionService } from "@/lib/permissions";

const userSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  name: z.string().min(1, "Name is required"),
  role: z.enum(["Admin", "Operator", "Accountant"], {
    required_error: "Role is required",
  }),
});

const editUserSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  password: z.string().min(6, "Password must be at least 6 characters").optional(),
  name: z.string().min(1, "Name is required"),
  role: z.enum(["Admin", "Operator", "Accountant"], {
    required_error: "Role is required",
  }),
});

type UserFormData = z.infer<typeof userSchema>;
type EditUserFormData = z.infer<typeof editUserSchema>;

export default function UserManagement() {
  const [open, setOpen] = useState(false);
  const [editingUser, setEditingUser] = useState<any>(null);
  const [managingPermissions, setManagingPermissions] = useState<any>(null);
  const [userPermissions, setUserPermissions] = useState<string[]>([]);
  const [searchTerm, setSearchTerm] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<UserFormData>({
    resolver: zodResolver(userSchema),
    defaultValues: {
      username: "",
      password: "",
      name: "",
      role: "Operator",
    },
  });

  const editForm = useForm<EditUserFormData>({
    resolver: zodResolver(editUserSchema),
    defaultValues: {
      username: "",
      password: "",
      name: "",
      role: "Operator",
    },
  });

  const { data: users, isLoading } = useQuery<any[]>({
    queryKey: ["/api/users"],
  });

  const createUserMutation = useMutation({
    mutationFn: async (data: UserFormData) => {
      const response = await authenticatedApiRequest("POST", "/api/users", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "User created",
        description: "User has been created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      setOpen(false);
      form.reset();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to create user",
        variant: "destructive",
      });
    },
  });

  const updateUserMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: EditUserFormData }) => {
      const response = await authenticatedApiRequest("PUT", `/api/users/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "User updated",
        description: "User has been updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      setEditingUser(null);
      editForm.reset();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to update user",
        variant: "destructive",
      });
    },
  });

  const deleteUserMutation = useMutation({
    mutationFn: async (id: string) => {
      await authenticatedApiRequest("DELETE", `/api/users/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "User deleted",
        description: "User has been deleted successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to delete user",
        variant: "destructive",
      });
    },
  });

  const filteredUsers = users?.filter((user: any) => {
    const searchString = searchTerm.toLowerCase();
    return (
      user.username.toLowerCase().includes(searchString) ||
      user.name.toLowerCase().includes(searchString) ||
      user.role.toLowerCase().includes(searchString)
    );
  }) || [];

  const getRoleBadgeVariant = (role: string) => {
    switch (role) {
      case "Admin":
        return "destructive";
      case "Operator":
        return "default";
      case "Accountant":
        return "secondary";
      default:
        return "default";
    }
  };

  const handleEdit = (user: any) => {
    setEditingUser(user);
    editForm.reset({
      username: user.username,
      name: user.name,
      role: user.role,
      password: "", // Don't prefill password
    });
  };

  const handleDelete = async (id: string) => {
    if (confirm("Are you sure you want to delete this user?")) {
      deleteUserMutation.mutate(id);
    }
  };

  // Define table columns
  const columns = [
    {
      accessorKey: "username",
      header: "Username",
      cell: (value: string) => <div className="font-medium">{value}</div>,
    },
    {
      accessorKey: "name",
      header: "Name",
      cell: (value: string) => value,
    },
    {
      accessorKey: "role",
      header: "Role",
      cell: (value: string) => (
        <Badge variant={getRoleBadgeVariant(value)}>
          {value}
        </Badge>
      ),
    },
    {
      accessorKey: "active",
      header: "Status",
      cell: (value: boolean) => (
        <Badge variant={value ? "default" : "secondary"}>
          {value ? "Active" : "Inactive"}
        </Badge>
      ),
    },
    {
      accessorKey: "id",
      header: "Actions",
      cell: (value: string, row: any) => (
        <div className="flex space-x-2">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleEdit(row)}
            data-testid={`button-edit-${value}`}
            title="Edit User"
          >
            <Edit className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setManagingPermissions(row)}
            data-testid={`button-permissions-${value}`}
            title="Manage Permissions"
          >
            <Shield className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleDelete(value)}
            data-testid={`button-delete-${value}`}
            title="Delete User"
            disabled={deleteUserMutation.isPending}
          >
            <Trash2 className="h-4 w-4" />
          </Button>
        </div>
      ),
    },
  ];

  const updatePermissionsMutation = useMutation({
    mutationFn: async ({ id, permissions }: { id: string; permissions: string[] }) => {
      const response = await authenticatedApiRequest("PUT", `/api/users/${id}/permissions`, { permissions });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Permissions updated",
        description: "User permissions have been updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to update permissions",
        variant: "destructive",
      });
    },
  });

  const handleManagePermissions = (user: any) => {
    setManagingPermissions(user);
    setUserPermissions(user.permissions || ROLE_PERMISSIONS[user.role as keyof typeof ROLE_PERMISSIONS] || []);
  };

  const togglePermission = (permission: string) => {
    setUserPermissions(prev => {
      if (prev.includes(permission)) {
        return prev.filter(p => p !== permission);
      } else {
        return [...prev, permission];
      }
    });
  };

  const savePermissions = () => {
    if (managingPermissions) {
      updatePermissionsMutation.mutate({
        id: managingPermissions.id,
        permissions: userPermissions
      });
      setManagingPermissions(null);
    }
  };

  const onSubmit = (data: UserFormData) => {
    createUserMutation.mutate(data);
  };

  const onEditSubmit = (data: EditUserFormData) => {
    if (!editingUser) return;

    // Only include password if it's provided
    const updateData = data.password 
      ? data 
      : { username: data.username, name: data.name, role: data.role };

    updateUserMutation.mutate({ id: editingUser.id, data: updateData });
  };

  return (
    <div className="flex h-screen">
      <Sidebar />

      <div className="flex-1 flex flex-col">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-semibold text-foreground">User Management</h2>
              <p className="text-sm text-muted-foreground">
                Manage system users and their permissions
              </p>
            </div>
            <Dialog open={open} onOpenChange={setOpen}>
              <DialogTrigger asChild>
                <Button className="gap-2" data-testid="button-add-user">
                  <Plus className="h-4 w-4" />
                  Add User
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Create New User</DialogTitle>
                </DialogHeader>
                <Form {...form}>
                  <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                    <FormField
                      control={form.control}
                      name="username"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Username</FormLabel>
                          <FormControl>
                            <Input placeholder="Enter username" {...field} data-testid="input-username" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="password"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Password</FormLabel>
                          <FormControl>
                            <Input type="password" placeholder="Enter password" {...field} data-testid="input-password" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="name"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Full Name</FormLabel>
                          <FormControl>
                            <Input placeholder="Enter full name" {...field} data-testid="input-name" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="role"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Role</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-role">
                                <SelectValue placeholder="Select role" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="Admin">Admin</SelectItem>
                              <SelectItem value="Operator">Operator</SelectItem>
                              <SelectItem value="Accountant">Accountant</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <div className="flex justify-end space-x-2 pt-4">
                      <Button type="button" variant="outline" onClick={() => setOpen(false)} data-testid="button-cancel">
                        Cancel
                      </Button>
                      <Button type="submit" disabled={createUserMutation.isPending} data-testid="button-create-user">
                        {createUserMutation.isPending ? "Creating..." : "Create User"}
                      </Button>
                    </div>
                  </form>
                </Form>
              </DialogContent>
            </Dialog>
          </div>
        </header>

        {/* Content */}
        <main className="flex-1 overflow-auto p-6">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="flex items-center gap-2">
                  <Users className="h-5 w-5" />
                  System Users
                </CardTitle>
                <div className="relative">
                  <Input
                    placeholder="Search users..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-64"
                    data-testid="input-search-users"
                  />
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <DataTable
                data={filteredUsers}
                columns={columns}
                searchTerm={searchTerm}
                searchFields={["username", "name", "role"]}
                isLoading={isLoading}
                enableRowSelection={true}
                rowKey="id"
              />
            </CardContent>
          </Card>
        </main>
      </div>

      {/* Edit User Modal */}
      <Dialog open={!!editingUser} onOpenChange={() => setEditingUser(null)}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Edit User</DialogTitle>
          </DialogHeader>
          <Form {...editForm}>
            <form onSubmit={editForm.handleSubmit(onEditSubmit)} className="space-y-4">
              <FormField
                control={editForm.control}
                name="username"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Username</FormLabel>
                    <FormControl>
                      <Input placeholder="Enter username" {...field} data-testid="input-edit-username" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={editForm.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Password (leave empty to keep current)</FormLabel>
                    <FormControl>
                      <Input type="password" placeholder="Enter new password" {...field} data-testid="input-edit-password" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={editForm.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Full Name</FormLabel>
                    <FormControl>
                      <Input placeholder="Enter full name" {...field} data-testid="input-edit-name" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={editForm.control}
                name="role"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Role</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-edit-role">
                          <SelectValue placeholder="Select role" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="Admin">Admin</SelectItem>
                        <SelectItem value="Operator">Operator</SelectItem>
                        <SelectItem value="Accountant">Accountant</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <div className="flex justify-end space-x-2 pt-4">
                <Button type="button" variant="outline" onClick={() => setEditingUser(null)} data-testid="button-cancel-edit">
                  Cancel
                </Button>
                <Button type="submit" disabled={updateUserMutation.isPending} data-testid="button-update-user">
                  {updateUserMutation.isPending ? "Updating..." : "Update User"}
                </Button>
              </div>
            </form>
          </Form>
        </DialogContent>
      </Dialog>

      {/* Permissions Management Modal */}
      <Dialog open={!!managingPermissions} onOpenChange={() => setManagingPermissions(null)}>
        <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Manage Permissions - {managingPermissions?.name}</DialogTitle>
            <p className="text-sm text-muted-foreground">
              Role: <Badge variant={getRoleBadgeVariant(managingPermissions?.role)}>
                {managingPermissions?.role}
              </Badge>
            </p>
          </DialogHeader>

          {managingPermissions && (
            <div className="space-y-6">
              <div className="grid grid-cols-1 gap-4">
                <div className="space-y-4">
                  <h4 className="text-lg font-medium">Default Role Permissions</h4>
                  <div className="grid grid-cols-2 gap-2">
                    {ROLE_PERMISSIONS[managingPermissions.role as keyof typeof ROLE_PERMISSIONS]?.map((permission: string) => (
                      <div key={permission} className="flex items-center space-x-2 p-2 bg-secondary rounded-md">
                        <Shield className="h-4 w-4 text-green-600" />
                        <span className="text-sm">{permission.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}</span>
                      </div>
                    )) || []}
                  </div>
                </div>

                <div className="space-y-4 border-t pt-4">
                  <h4 className="text-lg font-medium">Customize User Permissions</h4>
                  <p className="text-sm text-muted-foreground">
                    Toggle individual permissions on or off. Changes override the default role permissions.
                  </p>
                  <div className="grid grid-cols-1 gap-3">
                    {Object.entries({
                      "User Management": [PERMISSIONS.MANAGE_USERS, PERMISSIONS.VIEW_USERS],
                      "Vendor Management": [PERMISSIONS.MANAGE_VENDORS, PERMISSIONS.VIEW_VENDORS, PERMISSIONS.DELETE_VENDORS],
                      "Item Management": [PERMISSIONS.MANAGE_ITEMS, PERMISSIONS.VIEW_ITEMS, PERMISSIONS.DELETE_ITEMS],
                      "Purchase Invoices": [PERMISSIONS.CREATE_PURCHASE_INVOICES, PERMISSIONS.VIEW_PURCHASE_INVOICES, PERMISSIONS.EDIT_PURCHASE_INVOICES, PERMISSIONS.DELETE_PURCHASE_INVOICES],
                      "Payments": [PERMISSIONS.CREATE_PAYMENTS, PERMISSIONS.VIEW_PAYMENTS, PERMISSIONS.EDIT_PAYMENTS, PERMISSIONS.DELETE_PAYMENTS],
                      "Stock Management": [PERMISSIONS.MANAGE_STOCK, PERMISSIONS.VIEW_STOCK],
                      "Financial Reports": [PERMISSIONS.VIEW_LEDGERS, PERMISSIONS.VIEW_REPORTS, PERMISSIONS.VIEW_CASHBOOK, PERMISSIONS.VIEW_BANKBOOK],
                      "Bank Accounts": [PERMISSIONS.MANAGE_BANK_ACCOUNTS, PERMISSIONS.VIEW_BANK_ACCOUNTS],
                      "System": [PERMISSIONS.MANAGE_SETTINGS, PERMISSIONS.VIEW_SETTINGS, PERMISSIONS.VIEW_DASHBOARD, PERMISSIONS.VIEW_ANALYTICS],
                    }).map(([category, permissions]) => (
                      <div key={category} className="border rounded-lg p-3">
                        <h5 className="font-medium mb-3">{category}</h5>
                        <div className="space-y-3">
                          {permissions.map((permission: string) => {
                            const hasPermission = userPermissions.includes(permission);
                            return (
                              <div key={permission} className="flex items-center justify-between p-2 rounded-md border bg-card">
                                <div className="flex items-center space-x-2">
                                  <span className="text-sm font-medium">
                                    {permission.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}
                                  </span>
                                </div>
                                <Switch
                                  checked={hasPermission}
                                  onCheckedChange={() => togglePermission(permission)}
                                  data-testid={`switch-${permission}`}
                                />
                              </div>
                            );
                          })}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              <div className="flex justify-between items-center pt-4 border-t">
                <div className="text-sm text-muted-foreground">
                  {userPermissions.length} permissions selected
                </div>
                <div className="flex space-x-2">
                  <Button variant="outline" onClick={() => setManagingPermissions(null)} data-testid="button-close-permissions">
                    Cancel
                  </Button>
                  <Button 
                    onClick={savePermissions} 
                    disabled={updatePermissionsMutation.isPending}
                    data-testid="button-save-permissions"
                  >
                    {updatePermissionsMutation.isPending ? "Saving..." : "Save Permissions"}
                  </Button>
                </div>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

