      icon: Clock,
      iconBg: "bg-chart-1/10",
      iconColor: "text-chart-1",
      changeColor: "text-chart-1",
    },
    {
      title: "Active Vendors",
      value: kpis.activeVendors.toString(),
      change: "3 new this week",
      icon: Truck,
      iconBg: "bg-chart-3/10",
      iconColor: "text-chart-3",
      changeColor: "text-chart-3",
    },
    {
      title: "Stock Value",
      value: kpis.stockValue,
      change: kpis.totalStockKgs + " total",
      icon: Boxes,
      iconBg: "bg-chart-4/10",
      iconColor: "text-chart-4",
      changeColor: "text-chart-4",
    },
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
      {cards.map((card) => (
        <Card key={card.title} data-testid={`card-${card.title.toLowerCase().replace(/\s+/g, '-')}`}>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">{card.title}</p>
                <p className="text-2xl font-semibold text-foreground">{card.value}</p>
                <p className={`text-sm mt-1 ${card.changeColor}`}>{card.change}</p>
              </div>
              <div className={`w-12 h-12 ${card.iconBg} rounded-lg flex items-center justify-center`}>
                <card.icon className={`text-lg ${card.iconColor}`} />
              </div>
            </div>
          </CardContent>
        </Card>
      ))}
    </div>
  );
}

client/src/components/dashboard/low-stoc.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertTriangle } from "lucide-react";

export default function LowStockAlert() {
  const { data: stock, isLoading } = useQuery<any[]>({
    queryKey: ["/api/stock"],
  });

  if (isLoading) return null;

  // Filter items with low stock (less than 20 units)
  const lowStockItems = stock?.filter((item: any) => {
    const totalQty = parseFloat(item.quantityInKgs) + parseFloat(item.quantityInCrates);
    return totalQty < 20 && totalQty > 0;
  }) || [];

  if (lowStockItems.length === 0) return null;

  return (
    <Card>
      <CardHeader>
        <CardTitle>Low Stock Alert</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {lowStockItems.slice(0, 3).map((item: any) => {
            const remainingQty = item.commodity.unit === "Kgs" 
              ? parseFloat(item.quantityInKgs)
              : parseFloat(item.quantityInCrates);

            return (
              <Alert key={item.id} variant="destructive" data-testid={`alert-low-stock-${item.id}`}>
                <AlertTriangle className="h-4 w-4" />
                <AlertDescription>
                  <div className="font-medium">{item.commodity.name} {item.commodity.quality}</div>
                  <div className="text-sm">{remainingQty} {item.commodity.unit} remaining</div>
                </AlertDescription>
              </Alert>
            );
          })}
        </div>
      </CardContent>
    </Card>
  );
}

