          {selectedInvoice && (
            <div className="space-y-4">
              <div className="bg-muted p-4 rounded-lg">
                <div className="text-sm space-y-1">
                  <div><strong>Invoice:</strong> {selectedInvoice.invoiceNumber}</div>
                  <div><strong>Retailer:</strong> {getRetailerName(selectedInvoice.retailerId)}</div>
                  <div><strong>Total Amount:</strong> â‚¹{parseFloat(selectedInvoice.totalAmount).toLocaleString("en-IN")}</div>
                  <div><strong>Paid Amount:</strong> â‚¹{parseFloat(selectedInvoice.paidAmount).toLocaleString("en-IN")}</div>
                  <div><strong>Due Amount:</strong> â‚¹{parseFloat(selectedInvoice.balanceAmount).toLocaleString("en-IN")}</div>
                </div>
              </div>

              <div>
                <h4 className="font-medium mb-3">Payment Records</h4>
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Date</TableHead>
                      <TableHead>Amount</TableHead>
                      <TableHead>Mode</TableHead>
                      <TableHead>Reference</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {getInvoicePayments(selectedInvoice.id).map((payment: any) => (
                      <TableRow key={payment.id}>
                        <TableCell>{format(new Date(payment.paymentDate), "dd/MM/yyyy")}</TableCell>
                        <TableCell className="font-medium">â‚¹{parseFloat(payment.amount).toLocaleString("en-IN")}</TableCell>
                        <TableCell>
                          <Badge variant="outline">{payment.paymentMode}</Badge>
                        </TableCell>
                        <TableCell>{payment.transactionReference || "-"}</TableCell>
