import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import Sidebar from "@/components/layout/sidebar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { DataTable } from "@/components/ui/data-table";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useToast } from "@/hooks/use-toast";
import { authenticatedApiRequest } from "@/lib/auth";
import { z } from "zod";
import { 
  Plus, 
  Package, 
  ArrowUpCircle, 
  ArrowDownCircle, 
  Users, 
  TrendingUp,
  Calendar,
  AlertTriangle
} from "lucide-react";
import { format } from "date-fns";

const crateTransactionSchema = z.object({
  retailerId: z.string().min(1, "Retailer is required"),
  transactionType: z.enum(["Given", "Returned"]),
  quantity: z.number().min(1, "Quantity must be at least 1"),
  transactionDate: z.string().min(1, "Transaction date is required"),
  salesInvoiceId: z.string().optional(),
  notes: z.string().optional(),
  withDeposit: z.boolean().default(false),
  depositAmount: z.number().optional(),
}).refine((data) => {
  if (data.withDeposit && (!data.depositAmount || data.depositAmount <= 0)) {
    return false;
  }
  return true;
}, {
  message: "Deposit amount is required when with deposit is selected",
  path: ["depositAmount"],
});

type CrateTransactionFormData = z.infer<typeof crateTransactionSchema>;

export default function CrateManagement() {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedRetailer, setSelectedRetailer] = useState("all");
  const [selectedTransactionType, setSelectedTransactionType] = useState("all");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<CrateTransactionFormData>({
    resolver: zodResolver(crateTransactionSchema),
    defaultValues: {
      retailerId: "",
      transactionType: "Given",
      quantity: 1,
      transactionDate: format(new Date(), "yyyy-MM-dd"),
      salesInvoiceId: "",
      notes: "",
      withDeposit: false,
      depositAmount: undefined,
    },
  });

  // Fetch data
  const { data: crateTransactions = [], isLoading: transactionsLoading } = useQuery({
    queryKey: ["/api/crate-transactions"],
    queryFn: async () => {
      const response = await authenticatedApiRequest("GET", "/api/crate-transactions");
      return response.json();
    },
  });

  const { data: retailers = [] } = useQuery({
    queryKey: ["/api/retailers"],
    queryFn: async () => {
      const response = await authenticatedApiRequest("GET", "/api/retailers");
      return response.json();
    },
  });

  const { data: salesInvoices = [] } = useQuery({
    queryKey: ["/api/sales-invoices"],
    queryFn: async () => {
      const response = await authenticatedApiRequest("GET", "/api/sales-invoices");
      return response.json();
    },
  });

  // Create crate transaction
  const createTransactionMutation = useMutation({
    mutationFn: async (data: CrateTransactionFormData) => {
      const transactionData = {
        ...data,
        salesInvoiceId: data.salesInvoiceId || null,
        depositAmount: data.withDeposit ? data.depositAmount : null,
      };
      const response = await authenticatedApiRequest("POST", "/api/crate-transactions", transactionData);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Crate transaction recorded",
        description: "Crate transaction has been recorded successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/crate-transactions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/retailers"] });
      setDialogOpen(false);
      form.reset();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to record crate transaction",
        variant: "destructive",
      });
    },
  });

  const handleCreateTransaction = () => {
    form.reset({
      retailerId: "",
      transactionType: "Given",
      quantity: 1,
      transactionDate: format(new Date(), "yyyy-MM-dd"),
      salesInvoiceId: "",
      notes: "",
      withDeposit: false,
      depositAmount: undefined,
    });
    setDialogOpen(true);
  };

  const onSubmit = (data: CrateTransactionFormData) => {
    createTransactionMutation.mutate(data);
  };

  const getRetailerName = (retailerId: string) => {
    const retailer = retailers.find((r: any) => r.id === retailerId);
    return retailer?.name || "Unknown Retailer";
  };

  const getSalesInvoiceNumber = (invoiceId: string) => {
    const invoice = salesInvoices.find((i: any) => i.id === invoiceId);
    return invoice?.invoiceNumber || "N/A";
  };

  const getTransactionTypeColor = (type: string) => {
    return type === "Given" ? "bg-blue-500" : "bg-green-500";
  };

  const getTransactionTypeIcon = (type: string) => {
    return type === "Given" ? 
      <ArrowUpCircle className="h-4 w-4" /> : 
      <ArrowDownCircle className="h-4 w-4" />;
  };

  // Filter transactions
  const filteredTransactions = crateTransactions.filter((transaction: any) => {
    const matchesSearch = getRetailerName(transaction.retailerId).toLowerCase().includes(searchTerm.toLowerCase()) ||
                         transaction.notes?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesRetailer = selectedRetailer === "all" || transaction.retailerId === selectedRetailer;
    const matchesType = selectedTransactionType === "all" || transaction.transactionType === selectedTransactionType;
    return matchesSearch && matchesRetailer && matchesType;
  });

  // Calculate retailer crate balances
  const retailerCrateBalances = retailers.map((retailer: any) => {
    const retailerTransactions = crateTransactions.filter((t: any) => t.retailerId === retailer.id);
    const given = retailerTransactions
      .filter((t: any) => t.transactionType === "Given")
      .reduce((sum: number, t: any) => sum + t.quantity, 0);
    const returned = retailerTransactions
      .filter((t: any) => t.transactionType === "Returned")
      .reduce((sum: number, t: any) => sum + t.quantity, 0);
    const balance = given - returned;

    return {
      ...retailer,
      given,
      returned,
      balance,
    };
  });

  // Define transactions table columns
  const transactionColumns = [
    {
      accessorKey: "transactionDate",
      header: "Date",
      cell: (value: string) => format(new Date(value), "dd/MM/yyyy"),
    },
    {
      accessorKey: "retailerId",
      header: "Retailer",
      cell: (value: string) => getRetailerName(value),
    },
    {
      accessorKey: "transactionType",
      header: "Type",
      cell: (value: string) => (
        <div className="flex items-center space-x-2">
          {getTransactionTypeIcon(value)}
          <Badge className={getTransactionTypeColor(value)}>
            {value}
          </Badge>
        </div>
      ),
    },
    {
      accessorKey: "quantity",
      header: "Quantity",
      cell: (value: string) => <div className="font-medium">{value} crates</div>,
    },
    {
      accessorKey: "withDeposit",
      header: "Deposit",
      cell: (value: boolean, row: any) => {
        if (value && row.depositAmount) {
          return <Badge variant="outline">â‚¹{parseFloat(row.depositAmount).toLocaleString("en-IN")}</Badge>;
        }
        return <span className="text-muted-foreground">No deposit</span>;
      },
    },
    {
      accessorKey: "salesInvoiceId",
      header: "Related Invoice",
      cell: (value: string) => value ? getSalesInvoiceNumber(value) : "-",
    },
    {
      accessorKey: "notes",
      header: "Notes",
      cell: (value: string) => value || "-",
    },
  ];

  // Define retailer balances table columns
  const balanceColumns = [
    {
      accessorKey: "name",
      header: "Retailer",
      cell: (value: string) => <div className="font-medium">{value}</div>,
    },
    {
      accessorKey: "given",
      header: "Crates Given",
      cell: (value: number) => <div className="text-blue-600">{value}</div>,
    },
    {
      accessorKey: "returned",
      header: "Crates Returned",
      cell: (value: number) => <div className="text-green-600">{value}</div>,
    },
    {
      accessorKey: "balance",
      header: "Balance",
      cell: (value: number) => (
        <div className={`font-medium ${value > 0 ? 'text-orange-600' : value < 0 ? 'text-red-600' : 'text-gray-600'}`}>
          {value} crates
        </div>
      ),
    },
    {
      accessorKey: "balance",
      header: "Status",
      cell: (value: number) => {
        if (value > 0) {
          return (
            <div className="flex items-center space-x-1">
              <AlertTriangle className="h-4 w-4 text-orange-500" />
              <Badge variant="destructive">Outstanding</Badge>
            </div>
          );
        } else if (value < 0) {
          return <Badge variant="secondary">Excess Return</Badge>;
        } else {
          return <Badge variant="default">Settled</Badge>;
        }
      },
    },
  ];

  // Calculate summary stats
  const totalTransactions = crateTransactions.length;
  const totalCratesGiven = crateTransactions
    .filter((t: any) => t.transactionType === "Given")
    .reduce((sum: number, t: any) => sum + t.quantity, 0);
  const totalCratesReturned = crateTransactions
    .filter((t: any) => t.transactionType === "Returned")
    .reduce((sum: number, t: any) => sum + t.quantity, 0);
  const totalCratesOutstanding = totalCratesGiven - totalCratesReturned;
  const retailersWithCrates = retailerCrateBalances.filter((r: any) => r.balance > 0).length;

  if (transactionsLoading) {
    return (
      <div className="flex h-screen">
        <Sidebar />
        <div className="flex-1 p-8">
          <div className="animate-pulse space-y-4">
            <div className="h-8 bg-gray-200 rounded w-1/4"></div>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
              {[1, 2, 3, 4].map((i) => (
                <div key={i} className="h-24 bg-gray-200 rounded"></div>
              ))}
            </div>
            <div className="h-96 bg-gray-200 rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-semibold text-foreground">Crate Management</h2>
              <p className="text-sm text-muted-foreground">
                Track crate transactions with retailers
              </p>
            </div>
            <Button onClick={handleCreateTransaction} data-testid="button-add-transaction">
              <Plus className="h-4 w-4 mr-2" />
              Record Transaction
            </Button>
          </div>
        </header>

        {/* Content */}
        <main className="flex-1 overflow-auto p-6">
          <div className="max-w-7xl mx-auto space-y-8">

          {/* Summary Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Total Transactions</CardTitle>
                <Package className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{totalTransactions}</div>
                <p className="text-xs text-muted-foreground">All crate movements</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Crates Given</CardTitle>
                <ArrowUpCircle className="h-4 w-4 text-blue-600" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-blue-600">{totalCratesGiven}</div>
                <p className="text-xs text-muted-foreground">Total dispatched</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Crates Returned</CardTitle>
                <ArrowDownCircle className="h-4 w-4 text-green-600" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-green-600">{totalCratesReturned}</div>
                <p className="text-xs text-muted-foreground">Total received back</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Outstanding</CardTitle>
                <AlertTriangle className="h-4 w-4 text-amber-600" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-amber-600">{totalCratesOutstanding}</div>
                <p className="text-xs text-muted-foreground">With retailers</p>
              </CardContent>
            </Card>
          </div>

          {/* Main Content Tabs */}
          <Tabs defaultValue="transactions" className="space-y-6">
            <TabsList>
              <TabsTrigger value="transactions">Transactions</TabsTrigger>
              <TabsTrigger value="balances">Retailer Balances</TabsTrigger>
            </TabsList>

            <TabsContent value="transactions" className="space-y-6">
              <Card>
                <CardHeader>
                  <div className="flex justify-between items-center">
                    <CardTitle>Crate Transactions</CardTitle>
                    <div className="flex items-center space-x-2">
                      <Select value={selectedRetailer} onValueChange={setSelectedRetailer}>
                        <SelectTrigger className="w-40" data-testid="select-retailer-filter">
                          <SelectValue placeholder="All Retailers" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="all">All Retailers</SelectItem>
                          {retailers.map((retailer: any) => (
                            <SelectItem key={retailer.id} value={retailer.id}>
                              {retailer.name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <Select value={selectedTransactionType} onValueChange={setSelectedTransactionType}>
                        <SelectTrigger className="w-32" data-testid="select-type-filter">
                          <SelectValue placeholder="All Types" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="all">All Types</SelectItem>
                          <SelectItem value="Given">Given</SelectItem>
                          <SelectItem value="Returned">Returned</SelectItem>
                        </SelectContent>
                      </Select>
                      <Input
                        placeholder="Search transactions..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="w-64"
                        data-testid="input-search"
                      />
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <DataTable
                    data={filteredTransactions}
                    columns={transactionColumns}
                    searchTerm={searchTerm}
                    searchFields={["retailerId", "notes"]}
                    isLoading={transactionsLoading}
                    enableRowSelection={true}
                    rowKey="id"
                  />
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="balances" className="space-y-6">
              <Card>
                <CardHeader>
                  <div className="flex justify-between items-center">
                    <CardTitle>Retailer Crate Balances</CardTitle>
                    <div className="text-sm text-muted-foreground">
                      {retailersWithCrates} retailers have crates
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <DataTable
                    data={retailerCrateBalances}
                    columns={balanceColumns}
                    searchTerm=""
                    searchFields={["name"]}
                    isLoading={false}
                    enableRowSelection={true}
                    rowKey="id"
                  />
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>
      </main>
    </div>

    {/* Record Transaction Dialog */}
      <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
        <DialogContent className="sm:max-w-lg">
          <DialogHeader>
            <DialogTitle>Record Crate Transaction</DialogTitle>
          </DialogHeader>

          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="retailerId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Retailer *</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-retailer">
                          <SelectValue placeholder="Select retailer" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {retailers.map((retailer: any) => (
                          <SelectItem key={retailer.id} value={retailer.id}>
                            {retailer.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="grid grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="transactionType"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Transaction Type *</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-transaction-type">
                            <SelectValue />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="Given">
                            <div className="flex items-center space-x-2">
                              <ArrowUpCircle className="h-4 w-4 text-blue-600" />
                              <span>Given</span>
                            </div>
                          </SelectItem>
                          <SelectItem value="Returned">
                            <div className="flex items-center space-x-2">
                              <ArrowDownCircle className="h-4 w-4 text-green-600" />
                              <span>Returned</span>
                            </div>
                          </SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="quantity"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Quantity *</FormLabel>
                      <FormControl>
                        <Input
                          type="number"
                          min="1"
                          {...field}
                          onChange={(e) => field.onChange(parseInt(e.target.value) || 1)}
                          data-testid="input-quantity"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              {/* Deposit Option */}
              <FormField
                control={form.control}
                name="withDeposit"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Deposit Option</FormLabel>
                    <FormControl>
                      <RadioGroup
                        onValueChange={(value) => field.onChange(value === "true")}
                        value={field.value ? "true" : "false"}
                        className="flex flex-row space-x-6"
                        data-testid="radio-deposit-option"
                      >
                        <div className="flex items-center space-x-2">
                          <RadioGroupItem value="false" id="without-deposit" />
                          <Label htmlFor="without-deposit">Without Deposit</Label>
                        </div>
                        <div className="flex items-center space-x-2">
                          <RadioGroupItem value="true" id="with-deposit" />
                          <Label htmlFor="with-deposit">With Deposit</Label>
                        </div>
                      </RadioGroup>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Deposit Amount (Conditional) */}
              {form.watch("withDeposit") && (
                <FormField
                  control={form.control}
                  name="depositAmount"
                  render={({ field }) => {
                    const transactionType = form.watch("transactionType");
                    const isGiven = transactionType === "Given";

                    return (
                      <FormItem>
                        <FormLabel className="flex items-center space-x-2">
                          <span>Deposit Amount *</span>
                          <span className={`text-xs px-2 py-1 rounded-full ${
                            isGiven 
                              ? "bg-green-100 text-green-700 dark:bg-green-900 dark:text-green-300" 
                              : "bg-red-100 text-red-700 dark:bg-red-900 dark:text-red-300"
                          }`}>
                            {isGiven ? "Received from retailer" : "Paid to retailer"}
                          </span>
                        </FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            step="0.01"
                            min="0.01"
                            placeholder={`Enter deposit amount ${isGiven ? "received" : "to pay"}`}
                            {...field}
                            onChange={(e) => field.onChange(parseFloat(e.target.value) || undefined)}
                            data-testid="input-deposit-amount"
                          />
                        </FormControl>
                        <FormMessage />
                        <p className="text-xs text-muted-foreground">
                          {isGiven 
                            ? "Amount received from retailer as deposit for crates given"
                            : "Amount to be paid back to retailer for returned crates"
                          }
                        </p>
                      </FormItem>
                    );
                  }}
                />
              )}

              <FormField
                control={form.control}
                name="transactionDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Transaction Date *</FormLabel>
                    <FormControl>
                      <Input type="date" {...field} data-testid="input-transaction-date" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="salesInvoiceId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Related Sales Invoice (Optional)</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-sales-invoice">
                          <SelectValue placeholder="Select sales invoice" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {salesInvoices.map((invoice: any) => (
                          <SelectItem key={invoice.id} value={invoice.id}>
                            {invoice.invoiceNumber} - {getRetailerName(invoice.retailerId)}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="notes"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Notes</FormLabel>
                    <FormControl>
                      <Textarea placeholder="Additional notes..." {...field} data-testid="input-notes" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="flex justify-end space-x-2 pt-4">
                <Button type="button" variant="outline" onClick={() => setDialogOpen(false)} data-testid="button-cancel">
                  Cancel
                </Button>
                <Button 
                  type="submit" 
                  disabled={createTransactionMutation.isPending}
                  data-testid="button-submit"
                >
                  {createTransactionMutation.isPending ? "Recording..." : "Record Transaction"}
                </Button>
              </div>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/expenses.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import Sidebar from "@/components/layout/sidebar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { DataTable } from "@/components/ui/data-table";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useToast } from "@/hooks/use-toast";
import { authenticatedApiRequest } from "@/lib/auth";
import { z } from "zod";
import { 
  Plus, 
  Edit, 
  Trash2, 
  Receipt, 
  TrendingDown, 
  Calendar, 
  DollarSign,
  Tag,
  FileText,
  CreditCard
} from "lucide-react";
import { format } from "date-fns";

const expenseCategorySchema = z.object({
  name: z.string().min(1, "Category name is required"),
  description: z.string().optional(),
});

const expenseSchema = z.object({
  categoryId: z.string().min(1, "Category is required"),
  description: z.string().min(1, "Description is required"),
  amount: z.number().min(0.01, "Amount must be greater than 0"),
  paymentDate: z.string().min(1, "Payment date is required"),
  paymentMode: z.enum(["Cash", "Bank", "UPI", "Card"]),
  bankAccountId: z.string().optional(),
  notes: z.string().optional(),
});

type ExpenseCategoryFormData = z.infer<typeof expenseCategorySchema>;
type ExpenseFormData = z.infer<typeof expenseSchema>;

export default function ExpenseManagement() {
  const [expenseDialogOpen, setExpenseDialogOpen] = useState(false);
  const [categoryDialogOpen, setCategoryDialogOpen] = useState(false);
  const [editingExpense, setEditingExpense] = useState<any>(null);
  const [editingCategory, setEditingCategory] = useState<any>(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [selectedPaymentMode, setSelectedPaymentMode] = useState("all");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const expenseForm = useForm<ExpenseFormData>({
    resolver: zodResolver(expenseSchema),
    defaultValues: {
      categoryId: "",
      description: "",
      amount: 0,
      paymentDate: format(new Date(), "yyyy-MM-dd"),
      paymentMode: "Cash",
      bankAccountId: "",
      notes: "",
    },
  });

  const categoryForm = useForm<ExpenseCategoryFormData>({
    resolver: zodResolver(expenseCategorySchema),
    defaultValues: {
      name: "",
      description: "",
    },
  });

  // Fetch data
  const { data: expenses = [], isLoading: expensesLoading } = useQuery({
    queryKey: ["/api/expenses"],
    queryFn: async () => {
      const response = await authenticatedApiRequest("GET", "/api/expenses");
      return response.json();
    },
  });

  const { data: categories = [], isLoading: categoriesLoading } = useQuery({
    queryKey: ["/api/expense-categories"],
    queryFn: async () => {
      const response = await authenticatedApiRequest("GET", "/api/expense-categories");
      return response.json();
    },
  });

  const { data: bankAccounts = [] } = useQuery({
    queryKey: ["/api/bank-accounts"],
    queryFn: async () => {
      const response = await authenticatedApiRequest("GET", "/api/bank-accounts");
      return response.json();
    },
  });

  // Expense mutations
  const createExpenseMutation = useMutation({
    mutationFn: async (data: ExpenseFormData) => {
      const expenseData = {
        ...data,
        amount: data.amount.toFixed(2), // Convert number to string
        bankAccountId: data.bankAccountId || null,
      };
      const response = await authenticatedApiRequest("POST", "/api/expenses", expenseData);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Expense added",
        description: "New expense has been recorded successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/expenses"] });
      setExpenseDialogOpen(false);
      expenseForm.reset();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to add expense",
        variant: "destructive",
      });
    },
  });

  // Category mutations
  const createCategoryMutation = useMutation({
    mutationFn: async (data: ExpenseCategoryFormData) => {
      const response = await authenticatedApiRequest("POST", "/api/expense-categories", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Category created",
        description: "New expense category has been created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/expense-categories"] });
      setCategoryDialogOpen(false);
      categoryForm.reset();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to create category",
        variant: "destructive",
      });
    },
  });

  const updateCategoryMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<ExpenseCategoryFormData> }) => {
      const response = await authenticatedApiRequest("PUT", `/api/expense-categories/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Category updated",
        description: "Expense category has been updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/expense-categories"] });
      setCategoryDialogOpen(false);
      setEditingCategory(null);
      categoryForm.reset();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to update category",
        variant: "destructive",
      });
    },
  });

  const deleteCategoryMutation = useMutation({
    mutationFn: async (id: string) => {
      await authenticatedApiRequest("DELETE", `/api/expense-categories/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "Category deleted",
        description: "Expense category has been deleted successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/expense-categories"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to delete category",
        variant: "destructive",
      });
    },
  });

  const handleCreateExpense = () => {
    setEditingExpense(null);
    expenseForm.reset({
      categoryId: "",
      description: "",
      amount: 0,
      paymentDate: format(new Date(), "yyyy-MM-dd"),
      paymentMode: "Cash",
      bankAccountId: "",
      notes: "",
    });
    setExpenseDialogOpen(true);
  };

  const handleCreateCategory = () => {
    setEditingCategory(null);
    categoryForm.reset();
    setCategoryDialogOpen(true);
  };

  const handleEditCategory = (category: any) => {
    setEditingCategory(category);
    categoryForm.reset({
      name: category.name,
      description: category.description || "",
    });
    setCategoryDialogOpen(true);
  };

  const handleDeleteCategory = (id: string) => {
    if (confirm("Are you sure you want to delete this category? This action cannot be undone.")) {
      deleteCategoryMutation.mutate(id);
    }
  };

  const onSubmitExpense = (data: ExpenseFormData) => {
    createExpenseMutation.mutate(data);
  };

  const onSubmitCategory = (data: ExpenseCategoryFormData) => {
    if (editingCategory) {
      updateCategoryMutation.mutate({ id: editingCategory.id, data });
    } else {
      createCategoryMutation.mutate(data);
    }
  };

  const getCategoryName = (categoryId: string) => {
    const category = categories.find((c: any) => c.id === categoryId);
    return category?.name || "Unknown Category";
  };

  const getPaymentModeColor = (mode: string) => {
    switch (mode) {
      case "Cash":
        return "bg-green-500";
      case "Bank":
        return "bg-blue-500";
      case "UPI":
        return "bg-purple-500";
      case "Card":
        return "bg-orange-500";
      default:
        return "bg-gray-500";
    }
  };

  // Filter expenses
  const filteredExpenses = expenses.filter((expense: any) => {
    const matchesSearch = expense.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         getCategoryName(expense.categoryId).toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === "all" || expense.categoryId === selectedCategory;
    const matchesPaymentMode = selectedPaymentMode === "all" || expense.paymentMode === selectedPaymentMode;
    return matchesSearch && matchesCategory && matchesPaymentMode;
  });

  // Define expense table columns
  const expenseColumns = [
    {
      accessorKey: "paymentDate",
      header: "Date",
      cell: (value: string, row: any) => {
        try {
          const dateField = row.expenseDate || row.paymentDate;
          return format(new Date(dateField), "dd/MM/yyyy");
        } catch {
          return "Invalid Date";
        }
      },
    },
    {
      accessorKey: "description",
      header: "Description",
      cell: (value: string) => <div className="font-medium">{value}</div>,
    },
    {
      accessorKey: "categoryId",
      header: "Category",
      cell: (value: string) => (
        <Badge variant="outline">{getCategoryName(value)}</Badge>
      ),
    },
    {
      accessorKey: "amount",
      header: "Amount",
      cell: (value: string) => (
        <div className="font-medium text-red-600">
          â‚¹{parseFloat(value).toLocaleString("en-IN")}
        </div>
      ),
    },
    {
      accessorKey: "paymentMode",
      header: "Payment Mode",
      cell: (value: string) => (
        <Badge className={getPaymentModeColor(value)}>
          {value}
        </Badge>
      ),
    },
    {
      accessorKey: "notes",
      header: "Notes",
      cell: (value: string) => value || "-",
    },
  ];

  // Define category table columns  
  const categoryColumns = [
    {
      accessorKey: "name",
      header: "Name",
      cell: (value: string) => <div className="font-medium">{value}</div>,
    },
    {
      accessorKey: "description",
      header: "Description",
      cell: (value: string) => value || "-",
    },
    {
      accessorKey: "id",
      header: "Expenses Count",
      cell: (value: string) => {
        const categoryExpenses = expenses.filter((e: any) => e.categoryId === value);
        return <Badge variant="secondary">{categoryExpenses.length}</Badge>;
      },
    },
    {
      accessorKey: "id",
      header: "Total Amount",
      cell: (value: string) => {
        const categoryExpenses = expenses.filter((e: any) => e.categoryId === value);
        const categoryTotal = categoryExpenses.reduce((sum: number, exp: any) => 
          sum + parseFloat(exp.amount || "0"), 0
        );
        return <div className="font-medium">â‚¹{categoryTotal.toLocaleString("en-IN")}</div>;
      },
    },
    {
      accessorKey: "id",
      header: "Actions",
      cell: (value: string, row: any) => (
        <div className="flex space-x-2">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleEditCategory(row)}
            data-testid={`button-edit-category-${value}`}
            title="Edit Category"
          >
            <Edit className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleDeleteCategory(value)}
            data-testid={`button-delete-category-${value}`}
            title="Delete Category"
            disabled={deleteCategoryMutation.isPending}
          >
            <Trash2 className="h-4 w-4" />
          </Button>
        </div>
      ),
    },
  ];

  // Calculate summary stats
  const totalExpenses = expenses.length;
  const totalAmount = expenses.reduce((sum: number, expense: any) => 
    sum + parseFloat(expense.amount || "0"), 0
  );
  const todaysExpenses = expenses.filter((expense: any) => {
    try {
      const today = format(new Date(), "yyyy-MM-dd");
      // Handle both expenseDate and paymentDate fields
      const dateField = expense.expenseDate || expense.paymentDate;
      if (!dateField) return false;

      const expenseDate = format(new Date(dateField), "yyyy-MM-dd");
      return expenseDate === today;
    } catch (error) {
      // Skip expenses with invalid dates
      return false;
    }
  });
  const todaysAmount = todaysExpenses.reduce((sum: number, expense: any) => 
    sum + parseFloat(expense.amount || "0"), 0
  );
  const totalCategories = categories.length;

  if (expensesLoading || categoriesLoading) {
    return (
      <div className="flex h-screen">
        <Sidebar />
        <div className="flex-1 p-8">
          <div className="animate-pulse space-y-4">
            <div className="h-8 bg-gray-200 rounded w-1/4"></div>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
              {[1, 2, 3, 4].map((i) => (
                <div key={i} className="h-24 bg-gray-200 rounded"></div>
              ))}
            </div>
            <div className="h-96 bg-gray-200 rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <>
    <div className="flex h-screen">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-semibold text-foreground">Expense Management</h2>
              <p className="text-sm text-muted-foreground">
                Track business expenses and manage categories
              </p>
            </div>
            <div className="flex space-x-2">
              <Button variant="outline" onClick={handleCreateCategory} data-testid="button-add-category">
                <Tag className="h-4 w-4 mr-2" />
                Add Category
              </Button>
              <Button onClick={handleCreateExpense} data-testid="button-add-expense">
                <Plus className="h-4 w-4 mr-2" />
                Add Expense
              </Button>
            </div>
          </div>
        </header>

        {/* Content */}
        <main className="flex-1 overflow-auto p-6">
          <div className="max-w-7xl mx-auto space-y-8">

          {/* Summary Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Total Expenses</CardTitle>
                <Receipt className="h-4 w-4 text-muted-foreground" />
