                <CardContent>
                  <DataTable
                    data={categories}
                    columns={categoryColumns}
                    searchTerm=""
                    searchFields={["name", "description"]}
                    isLoading={categoriesLoading}
                    enableRowSelection={true}
                    rowKey="id"
                  />
